mapper.select:
SELECT S.SPACE_ID, S.OWNER_USER_ID, S.NAME, S.OPENED_TIME, S.END_TIME AS CLOSE_TIME from QES_SPACE S INNER JOIN QES_USER_JOIN UJ ON UJ.SPACE_ID = S.SPACE_ID WHERE UJ.USER_ID = #{userId}
AND S.SPACE_ID = #{spaceId}
ORDER BY S.END_TIME DESC

mapper.insert:
INSERT INTO QES_USER_JOIN
(
    USER_ID,
    SPACE_ID
) VALUES (
    #{userId},
    #{spaceId}
)
